import React from 'react';
import './App.css';
import Dog from './Dog'
import DogInfo from './DogInfo'



class App extends React.Component {
  constructor() {
    super();
    this.state = {
      pup: {}
    }
  }


  handleDogInfo = (pup) => {
    console.log("app pup:", this.state.pup)
    console.log("dog pup:", pup)
    this.setState({ pup: pup })
  }

  render() {
    return (
      <div className="App" >
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <div id="dog-bar">
          <Dog
            handleDogInfo={this.handleDogInfo}
          />
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            <DogInfo pup={this.state.pup} />
          </div>
        </div>
      </div>

    );
  }
}


export default App;
