import React, { Component } from "react";

export default class DoInfo extends Component {
    constructor() {
        super();
        this.state = {

        }
    }

    handleDogToggle = () => {
        let pup = this.props.pup
        pup.isGoodDog = !pup.isGoodDog
        console.log(pup)

        fetch(`http://localhost:3000/pups/${pup.id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(pup)
        })
            .then(resp => resp.json())
            .then(updatedPup => {
                console.log(updatedPup)
            })

    }

    render() {
        let pup = this.props.pup
        return (
            <React.Fragment>
                <img src={pup.image} alt={pup.name}></img>
                <h2>{pup.name}</h2>
                {pup.name != null && <button onClick={this.handleDogToggle}>{pup.isGoodDog ? "Good Dog!" : "Bad Dog!"}</button>}
            </React.Fragment>
        )
    }
}
