import React, { Component } from "react"


export default class Dog extends Component {
    constructor() {
        super()
        this.state = {
            allPups: []
        }
    }


    componentDidMount() {
        fetch("http://localhost:3000/pups")
            .then(resp => resp.json())
            .then(pups => {
                this.setState({ allPups: pups })
            })
    }

    handleDogInfo = (event, pup) => {
        console.log(this.props.pup)
        this.props.setState({ pup: pup })
    }

    render() {
        return (
            <React.Fragment>
                {this.state.allPups.map((pup) => (
                    <span onClick={(event) => this.handleDogInfo(event, pup)} key={pup.id}>{pup.name}</span>
                ))}
            </React.Fragment>
        )
    }
}
