import React, { Component } from "react"

export default class Dog extends Component {
    constructor() {
        super()
        this.state = {
            allPups: ""
        }
    }


    fetchPups = () => {
        let that = this
        fetch("http://localhost:3000/pups")
            .then(resp => resp.json())
            .then(pups => {
                that.setState({ allPups: "hey" })
                console.log(pups)
            })
    }

    renderDogSpan = () => {
        this.fetchPups()
        console.log(this.state.allPups)
    }
    render() {
        return (this.renderDogSpan)
    }
}
